package day3.variableprograms;

public class Demo5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b = 20;
		float h = 12.5f;
		double area = 1.0/ 2 * b * h;
		System.out.println("Breadth = " + b);
		System.out.println("Height  = " + h);
		System.out.println("Area of Triangle =" + area);
	}

}
/* here if we take 1/2 in formula it gives us area=0
 this is because of complete number divide by complete number,
therefore we take that half in decimal as 0.5 or we write numerator/denominator in decimal as above*/